class OptimizedToolLoader {
  constructor() {
    this.loadedTools = new Map();
    this.toolUsageStats = new Map();
    this.toolDependencies = this.analyzeToolDependencies();
  }


  async loadTool(toolName) {
    // Check if already loaded
    if (this.loadedTools.has(toolName)) {
      this.updateToolUsageStats(toolName);
      return this.loadedTools.get(toolName);
    }

    // Load dependencies first
    const dependencies = this.toolDependencies[toolName] || [];
    await Promise.all(dependencies.map(dep => this.loadTool(dep)));

    // Load the actual tool
    const toolDefinition = await this.fetchToolDefinition(toolName);
    const compressedDefinition = this.compressToolDefinition(toolDefinition);

    // Initialize the tool
    const tool = await this.initializeTool(compressedDefinition);

    // Store and return
    this.loadedTools.set(toolName, tool);
    this.updateToolUsageStats(toolName);

    return tool;
