# src/database/health.py
def check_db_health():
    try:
        conn = get_db_connection()
        result = conn.execute('SELECT 1').fetchone()
        tables = conn.execute(
            "SELECT name FROM sqlite_master WHERE type='table'"
        ).fetchall()
        required_tables = ['model_costs', 'request_costs', 'conversation_costs']
        missing_tables = [t for t in required_tables if t not in [row[0] for row in tables]]

        return {
            'status': 'healthy' if not missing_tables else 'degraded',
            'connection': 'ok' if result else 'failed',
            'missing_tables': missing_tables
        }
    except Exception as e:
        return {'status': 'unhealthy', 'error': str(e)}
```

